    /*
     * Listing of contacts
     */
    function index()
    {
		$data = $this->session->userdata;
	if($data['role']!='admin'){
	        redirect(site_url().'signup');
	    }
        $params['limit'] = RECORDS_PER_PAGE; 
        $params['offset'] = ($this->input->get('per_page')) ? $this->input->get('per_page') : 0;
        
        $config = $this->config->item('pagination');
        $config['base_url'] = site_url('contacts/index?');
        $config['total_rows'] = $this->Contacts_model->get_all_contacts_count();
        $this->pagination->initialize($config);

        $data['contacts'] = $this->Contacts_model->get_all_contacts($params);
        
        $data['_view'] = 'contacts/index';
        $this->load->view('layouts/main2',$data);
    }
	
	/*
     * Adding a new chage status 
     */
	 
	 function status($status,$id)
    {
		$params = array('status' => $status);
		$params=$this->security->xss_clean($params);
		$this->Contacts_model->update_contacts($id,$params);        
		redirect(site_url().'contacts');
    }

