    /*
     * Editing a contacts
     */
    function edit($id)
    {   
			$data = $this->session->userdata;
	if($data['role']!='admin'){
					redirect(site_url().'signup');
				}
        // check if the contacts exists before trying to edit it
        $data['contacts'] = $this->Contacts_model->get_contacts($id);
        
        if(isset($data['contacts']['id']))
        {
            $this->load->library('form_validation');

			//
			$this->form_validation->set_rules('name','Name','trim|required|max_length[255]');
            $this->form_validation->set_rules('email','Email','trim|required|max_length[100]');
            $this->form_validation->set_rules('phn','Phn','trim|required|max_length[12]');
            $this->form_validation->set_rules('subject','Subject','trim|required|max_length[255]');
            $this->form_validation->set_rules('message','Message','trim|required');

		
			if($this->form_validation->run())     
            {   
                $params = array(
					//
					'name' => $this->input->post('name'),
					'email' => $this->input->post('email'),
					'phn' => $this->input->post('phn'),
					'subject' => $this->input->post('subject'),
					'message' => $this->input->post('message'),

                );
				$params=$this->security->xss_clean($params);
                $this->Contacts_model->update_contacts($id,$params);            
                redirect('contacts/index');
            }
            else
            {
                $data['_view'] = 'contacts/edit';
                $this->load->view('layouts/main2',$data);
            }
        }
        else
            show_error('The contacts you are trying to edit does not exist.');
    } 

    /*
     * Deleting contacts
     */
    function remove($id)
    {
        $contacts = $this->Contacts_model->get_contacts($id);

        // check if the contacts exists before trying to delete it
        if(isset($contacts['id']))
        {
            $this->Contacts_model->delete_contacts($id);
            redirect('contacts/index');
        }
        else
            show_error('The contacts you are trying to delete does not exist.');
    }
    public function sendRegister($vtoken){
        $message = '';
        $message .= 'Hi,<br>';
        $message .= '<br>';
        $message .= 'Welcome! you have signed up with our website with the following information:<br>';
        $message .= '<br>';
        $message .= '<strong>VALIDATION TOKEN : '.$vtoken.'</strong><br>';
        $message .= '<br>';
        $message .= 'Before you can login, you need to activate and set your Password';
        $message .= '<br>';
        $message .= 'Sincerely yours,<br>';
        $message .= 'Your Own Team :)';
        return $message;
    }
	public function base64url_encode($data) {
      return rtrim(strtr(base64_encode($data), '+/', '-_'), '=');
    }

    public function base64url_decode($data) {
      return base64_decode(str_pad(strtr($data, '-_', '+/'), strlen($data) % 4, '=', STR_PAD_RIGHT));
    }
	  function cvalidate()
    {
		$vcode=$_POST['vcode'];
        $token = base64_decode($vcode);
        $cleanToken = $this->security->xss_clean($token);

        $user_info = $this->Contacts_model->isTokenValid($cleanToken); //either false or array();

        if(!$user_info){
            $this->session->set_flashdata('flash_message', 'Token is invalid or expired');
            echo '<div style="width: 100%;" class="alert alert-warning alert-dismissible" role="alert">Token is not validated</div>';
        }else{
			echo '<div style="width: 100%;" class="alert alert-success alert-dismissible" role="alert">Thanks for validating, please check given below details submitted by you.</div>';
		}
	}
