class Course_lectures extends CI_Controller{
    function __construct()
    {
        parent::__construct();
        $this->load->model('Course_lectures_model');
		$this->load->model('Courses_model');
        $this->load->model('Users_model');
        $this->load->model('Curriculam_model');
		$data = $this->session->userdata;
	if($data['role']!='admin'){
	        redirect(site_url().'signup');
	    }
    } 

    /*
     * Listing of course_lectures
     */
    function index()
    {
        $params['limit'] = RECORDS_PER_PAGE; 
        $params['offset'] = ($this->input->get('per_page')) ? $this->input->get('per_page') : 0;
        
        $config = $this->config->item('pagination');
        $config['base_url'] = site_url('course_lectures/index?');
        $config['total_rows'] = $this->Course_lectures_model->get_all_course_lectures_count();
        $this->pagination->initialize($config);

        $data['course_lectures'] = $this->Course_lectures_model->get_all_course_lectures($params);
        
        $data['_view'] = 'course_lectures/index';
        $this->load->view('layouts/main2',$data);
    }

    /*
     * Adding a new course_lectures ffffff
     */
    function add()
    {   
        $this->load->library('form_validation');

		//
		$this->form_validation->set_rules('course_id','Course_id','trim|required|max_length[10]');
$this->form_validation->set_rules('studentid','Studentid','trim|required|max_length[10]');
$this->form_validation->set_rules('batch_id','Batch_id','trim|required|max_length[10]');
$this->form_validation->set_rules('datetimeof','Datetimeof','trim|required|max_length[10]');
$this->form_validation->set_rules('tid','Tid','trim|required|max_length[10]');
$this->form_validation->set_rules('attendance','Attendance','trim|required|max_length[1]');

		
		if($this->form_validation->run())     
